using namespace std;
bool prime(int n)
{
	int i,t=sqrt(n);
	for (i=2; i<=t; i++)
		if (n%i==0) break;
	return i>t;
}
int main()
{
	int n;
	cin>>n;
	if (prime(n)) cout<<"prime"<<endl;
	else cout<<"not prime"<<endl;
	return 0;
}

