#include<iostream>
using namespace std;
void swap(int *p,int *q)
{
	int t=*p;
	*p=*q;
	*q=t;
}
int main()
{
	int a,b,c;
	cin>>a>>b>>c;
	int *p1=&a,*p2=&b,*p3=&c;
	if (a>b) swap(p1,p2);
	if (a>c) swap(p1,p3);
	if (b>c) swap(p2,p3);
	cout<<*p1<<" "<<*p2<<" "<<*p3<<" "<<endl;
	return 0;
}

