                int sum=0;
                for(int j=0;j<idn[i];j++){
                    idnm[j]=sc.nextInt();
                    sum= sum + gm[idnm[j]];
                }
                if(sum>=g){
                    str[k++]=idstr[i]+" "+sum;
                }
            }

            if(k==0){
                System.out.println(0);
                continue;
            }
            //System.out.println("aaa");
