            for(int i=0;i<k;i++){
                String[] strTemp = new String[2];
                strTemp = str[i].split(" ");
                strs[i]=strTemp[0];
                strm[i]=Integer.parseInt(strTemp[1]);
            }

            for(int i=0;i<k-1;i++){
                for(int j=i+1;j<k;j++){
                    if(strm[i]<strm[j]){
                        strm[i]=strm[i]^strm[j];
                        strm[j]=strm[i]^strm[j];
                        strm[i]=strm[i]^strm[j];

                        String s=strs[i];
