#include <stdio.h>
#include <stdlib.h>
int f1(int a,int b)
{
    int tmp;
    if(a<b)
    {
        tmp=b;
        b=a;
        a=tmp;
    }
    while(a%b!=0)
    {
        tmp=a%b;
        a=b;
        b=tmp;
    }
    return b;
}

int f2(int a,int b)
{
    int t1,t2;
    t1=f1(a,b);
    t2=a*b/t1;
    return t2;
}

int main(int argc, char *argv[])
{
    int a,b;
    int t1,t2;
    scanf("%d%d",&a,&b);
    t1=f1(a,b);
    t2=f2(a,b);
    printf("%d %d",t1,t2);

  return 0;
}

