	}
	r->next=NULL;
	cin>>e;
	int t=Sort(e,C);
	cout<<t<<endl;
	return 0;
} 

int Sort(int e,BNode *C){
	BNode *r;
	//C->next=NULL;
	r=C;
	int i=1;
	while(r->next!=NULL){
		r=r->next;
		if(r->data==e){
			return i;
		}
		i++;
	} 
}

