#include<stdio.h>
#include<stdlib.h>
#define ElemType int
typedef struct LNode{
	ElemType data;
	struct LNode *next;
}LNode,*LinkList;
void initial(LinkList L){
	int n;
	ElemType data;
	LNode *p = L;
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		scanf("%d",&data);
		LNode *s = (LNode *)malloc(sizeof(LNode));
		s->data = data;
		p->next = s;
		p = p->next;
	}
	p->next = NULL;
}
void insert(LinkList L,ElemType elem){
	LNode *p = L;
	while(p->next){
		if(p->next->data>=elem){
			LNode *s = (LNode *)malloc(sizeof(LNode));
			s->data = elem;
			s->next = p->next;
			p->next = s;
			return;
		}
		p = p->next;
	}
	LNode *s = (LNode *)malloc(sizeof(LNode));
	s->data = elem;
	s->next = p->next;
	p->next = s;
}

