#include<iostream>

using namespace std;

struct Node
{
	int data;
	Node* pNext;
};

struct LinkList
{
	Node* pHead;
	Node* pTail;
};

void createList(LinkList *list)
{
	list->pHead=(Node*)malloc(sizeof(Node));
	list->pTail=list->pHead;
	list->pTail->pNext=NULL;

	int n;
	cin>>n;
	while(n--)
	{
		int val;
		cin>>val;
		Node* pNewNode;
		pNewNode=(Node*)malloc(sizeof(Node));
		pNewNode->data=val;
		list->pTail->pNext=pNewNode;
		pNewNode->pNext=NULL;
		list->pTail=pNewNode;
	}
}

void traverseList(LinkList *list)
{
	Node* p=list->pHead->pNext;
	for(;p!=NULL;p=p->pNext)
		cout<<p->data<<" ";
	cout<<endl;
}

int main()
{
	LinkList theList;
	createList(&theList);

	int val;
	cin>>val;

	for(Node* p=theList.pHead->pNext;p!=NULL;p=p->pNext)
	{
		if(val==(p->data))
		{
			Node* q;
			q=(Node*)malloc(sizeof(Node));
			q->data=val;
			q->pNext=p->pNext;
			p->pNext=q;
			break;
		}
	}

	Node* p=theList.pHead->pNext;
	for(;(p->pNext)!=NULL;p=p->pNext)
	{
		if(p->data<val&&(p->pNext->data)>val)
		{
			Node* q;
			q=(Node*)malloc(sizeof(Node));
			q->data=val;
			q->pNext=p->pNext;
			p->pNext=q;
			break;
		}
	}

	if(val<(theList.pHead->pNext->data))
	{
		Node* q;
		q=(Node*)malloc(sizeof(Node));
		q->data=val;
		q->pNext=theList.pHead->pNext;
		theList.pHead->pNext=q;
	}

	if(val>(theList.pTail->data))
	{

		Node* q;
		q=(Node*)malloc(sizeof(Node));
		q->data=val;
		theList.pTail->pNext=q;
		q->pNext=NULL;
		theList.pTail=q;

	}



	traverseList(&theList);
	//system("pause");
}

