int main()
{
	char a[110],b[110],ch,q,p;
	int i,count,j;
		for(i=0;;i++)
		{
			scanf("%c",&a[i]);
			if(a[i]=='.')
				break;
		}
		a[i+1]='\0';
		getchar();
		scanf("%c",&ch);
		getchar();
		if(ch=='D')
			scanf("%c",&q);
		else
			scanf("%c %c",&q,&p);
		getchar();
		if(ch=='D')
		{
			count=0,j=0;
			for(i=0;i<strlen(a);i++)
			{
				if(a[i]==q&&count==0)
				{
					count++;
				}
				else
				{
					b[j++]=a[i];
				}
			}
			b[j]='\0';
			if(count==0)
				printf("Not exist\n");
			else
			    printf("%s\n",b);
		}
		if(ch=='I')
		{
			count=0;j=strlen(a);
			for(i=strlen(a)-1;i>=0;i--)
			{
				if(a[i]==q&&count==0)
				{
					count++;
					b[j--]=a[i];
					b[j--]=p;
				}
				else
				{
					b[j--]=a[i];
				}
			}
			b[strlen(a)+1]='\0';
			if(count==0)
				printf("Not exist\n");
			else
			    printf("%s\n",b);
		}
		if(ch=='R')
		{
			count=0,j=0;
			for(i=0;i<strlen(a);i++)
			{
				if(a[i]==q)
				{
					b[j++]=p;
					count++;
				}
				else
					b[j++]=a[i];
			}
			b[j]='\0';
			if(count==0)
				printf("Not exist\n");
			else
				printf("%s\n",b);
		}
	return 0;
}

