#include<iostream>
using namespace std;
int main()
{
	int n;
	cin>>n;
	for (int i=6; i<=n; i++)
	{
		int sum=0;
		for (int j=1; j<=i/2; j++)
			if (i%j==0) sum+=j;
		if (i==sum)
		{
			cout<<i<<" its factors are ";
			for (int j=1; j<=i/2; j++)
				if (i%j==0) cout<<j<<" ";
			cout<<endl;
		}
	}
	return 0;
}
