#include<stdio.h>
#include<math.h>
int zong(int n)
{
	if(n==1)  return 1;
	else return (zong(n-1)+1)*2;
}
int main()
{
	int n;
	scanf("%d",&n);
	int m=zong(n);
	printf("%d",m);
}

