#include<stdio.h>
#include<math.h>
int main()
{
	int a[3][3];
	int i,j,sum;
	for(i=0;i<3;i++)
	  for(j=0;j<3;j++)
	{
		scanf("%d",&a[i][j]);
	}
	printf("%d %d",a[0][0]+a[1][1]+a[2][2],a[0][2]+a[1][1]+a[2][0]);
