#include<iostream>
using namespace std;
int a[4][4];
int main()
{
	for (int i=1; i<=3; i++)
		for (int j=1; j<=3; j++) cin>>a[i][j];
	int sum1=0,sum2=0;
	for (int i=1; i<=3; i++)
	{
		sum1+=a[i][i];
		sum2+=a[i][4-i];
	}
	cout<<sum1<<" "<<sum2<<endl;
	return 0;
}
