#include<iostream>
using namespace std;
int a[4][4];
void change(int a[][4])
{
	for (int i=1; i<=3; i++)
		for (int j=i+1; j<=3; j++)
		{
			int t=a[i][j];
			a[i][j]=a[j][i];
			a[j][i]=t;
		}
}
int main()
{
	for (int i=1; i<=3; i++)
		for (int j=1; j<=3; j++) cin>>a[i][j];
	change(a);
	for (int i=1; i<=3; i++)
	{
		for (int j=1; j<=3; j++) cout<<a[i][j]<<" ";
		cout<<endl;
	}
	return 0;
}
