#include<iostream>
#include<cstdio>
using namespace std;
char s1[201],s2[101];
void catenate(char a[],char b[])
{
	int i;
	for (i=0; a[i]!='\0'; i++) ;
	for (int j=0; b[j]!='\0'; j++)
		a[i++]=b[j];
	a[i]='\0';
}
int main()
{
	gets(s1);
	gets(s2);
	catenate(s1,s2);
	cout<<s1<<endl;
	return 0;
}
