#include<iostream>
#include<stdio.h>
#define	MAX(a,b,c) ((a>b?a:b)>c?(a>b?a:b):c)
using namespace std;
double max(double a,double b,double c)
{
	return (a>b?a:b)>c?(a>b?a:b):c;
}
int main()
{
	double a,b,c;
	cin>>a>>b>>c;
	printf("%.3lf\n",max(a,b,c));
	printf("%.3lf\n",MAX(a,b,c));
	return 0;
}
