#include<iostream>
using namespace std;
int a[50];
void move(int *q,int n,int m)
{
	int *p,t=*(q+n-1);
	for (p=q+n-1; p>q; p--) *p=*(p-1);
	*q=t;
	if (--m) move(a,n,m);	//递归
}
int main()
{
	int n,m;
	cin>>n;
	for (int i=0; i<n; i++) cin>>a[i];
	cin>>m;
	move(a,n,m);
	for (int i=0; i<n; i++) cout<<a[i]<<" ";
	cout<<endl;
	return 0;
}
