#include<iostream>
#include<cstring>
using namespace std;
bool a[1000];
int main()
{
	memset(a,0,sizeof(a));
	int n;
	cin>>n;
	bool *p=a;
	for (int i=0; i<n; i++) *(p+i)=true;
	int i=0;	//i为每次循环时计数变量
	int k=0;	//k为按1,2,3报数时的计数变量
	int m=0;	//m为退出人数
	while (m<n-1)
	{
		if (i==n) i=0;
		if (*(p+i)) k++;
		if (k==3)
		{
			*(p+i)=false;
			k=0;
			m++;
		}
		i++;
	}
	for (int i=0; i<n; i++)
		if (*(p+i)) { cout<<i+1<<endl; break; }
	return 0;
}
