#include<iostream>
#include<string>
using namespace std;
struct student{
	string num;
	string name;
	int score[3];
};
student a[105];
void input(student a[],int n)
{
	for (int i=0; i<n; i++)
		cin>>a[i].num>>a[i].name>>a[i].score[0]>>a[i].score[1]>>a[i].score[2];
}
void output(student a[],int n)
{
	for (int i=0; i<n; i++)
	{
		cout<<a[i].num<<","<<a[i].name;
		for (int j=0; j<3; j++) cout<<","<<a[i].score[j];
		cout<<endl;
	}
}
int main()
{
	int n;
	cin>>n;
	input(a,n);
	output(a,n);
	return 0;
}
