#include<bits/stdc++.h>
using namespace std;
struct f
{
	int a,b;
}a[100000];
bool cmp(f a,f b)
{
	return a.a<b.a;
}
int n,m;
int main()
{
	cin>>n>>m;
	for(int i=1;i<=n+m;i++)
	cin>>a[i].a>>a[i].b;
	sort(a+1,a+n+m+1,cmp);
	for(int i=1;i<=n+m;i++)
	cout<<a[i].a<<" "<<a[i].b<<"\n";
	return 0;
}
