public class Main {
	public static void main(String[] args) {
		for (int i = 1000; i < 1111; i++) {
             if(f(i)==i*9){
            	 System.out.println(i);
             }
		}

	}

	private static int f(int n) {
		int a[] = new int[4];
		int i = 3;
		while (n > 0) {
			a[i] = n % 10;
			n = n / 10;
			i-- ;
		}


		int s = 0 ;

		for (int j = 0; j < a.length; j++) {
			s = (int) (s + a[j]*Math.pow(10, j)) ;
		}

		return s ;
	}
}
