#include <stdio.h>
int main()
{
	long sum, n;
	int i;
	sum = 0;
	n = 1;
	for(i = 0; i < 30; i++)
	{
		sum += n;
		n *= 2;
	}
	printf("%ld %ld\n", sum / 100, 10*10000*30);
	return 0;
}

