#include<iostream>
#include <cstdio>
using namespace std;
int main()
{
    //freopen("data.out", "w", stdout);
    int a,b,c;
    for(int i=100; i<1000; i++)
    {
        a=i/100;
        b=i/10-a*10;
        c=i%10;
        if((i+b*100+c*10+c)==532)
        {
            cout<<a<<" "<<b<<" "<<c;
            cout<<endl;
        }
    }
    return 0;
}

public class Main {
    public static void main(String[] args) {
        for (int a = 0; a < 9; a++) {
            for (int b = 0; b < 9; b++) {
                for (int c = 0; c < 9; c++) {
                    if(a*100+b*10+c+b*100+c*10+c == 532){
                        System.out.print(a+" ");
                        System.out.print(b+" ");
                        System.out.print(c+" ");
                    }
                }
            }
        }
    }
}
